css:
@media all {
  .print-format {
    max-height: 210mm;
    max-width:297mm;
    size: A4 portrait;
  }
   table,
  th,
  td {
    font-size: 8px;
    border: 1px solid black;
    word-wrap: break-word;
    padding: 0px !important;
  }
  th
  {
    line-height: 1;
    font-size: inherit;
    border: 1px solid black;
  }
  table {
    width: 100%;
  }
  .print-format th {
    background-color: transparent !important;
  }
  body {
    line-height: unset;
  }

  .main{
    position: relative; 
    margin: 0;
    padding: 0;
    max-width: 297mm;
    max-height:210mm;
    padding-left:10px;
    word-wrap: break-word;
  }
  .header {
      text-align: center;
      font-size: 24;
      font-weight: bold;
  }
  .receipt-details {
      display: flex;
      width: 100%;
      justify-content: space-between;
  }
  .tenant-details {
      flex: 1;
      width: 100%;
      text-align: left;
  }
  .payment-details {
      flex: 1;
      width: 100%;
      text-align: right;
  }
  .label {
      width: 30%;
  }

  .data {
      width: 70%;
  }

}
